mapper.select:
SELECT COUNT(*) FROM t_board_list
WHERE icategory = ${icategory}
ORDER BY iboard DESC


SELECT
IFNULL(
        (
            SELECT iboard
            FROM t_board_list
            WHERE icategory = ${icategory}
            AND iboard > ${iboard}
            ORDER BY iboard
            LIMIT 1
        ), 0
        ) AS previboard,

IFNULL(
         (
            SELECT iboard
            FROM t_board_list
            WHERE icategory = ${icategory}
            AND iboard < ${iboard}
            ORDER BY iboard DESC
            LIMIT 1
            ) , 0
        )AS nextiboard

mapper.update:
UPDATE t_board_list


        title = #{title},


        ctnt = #{ctnt},


WHERE iboard = ${iboard}
AND iuser = ${iuser}

mapper.delete:
DELETE FROM t_board_list
WHERE iboard = ${iboard}
AND iuser = ${iuser}
