mapper.update:
UPDATE product_detail

    SET sm = sm - ${sm}


    SET md = md - ${md}


    SET lg = lg - ${lg}


    SET xl = xl - ${xl}

WHERE iboard = ${iboard} AND color = ${color}

mapper.insert:
insert into cart(iboard, uid, color,

    sm


    md


    lg


    xl
 )
VALUES (${iboard}, #{uid}, ${color},

${sm}


${md}


${lg}


${xl}
 )
