mapper.select:
SELECT *
FROM product_list
WHERE category_num = ${isubcategory}
ORDER BY iboard ASC
LIMIT ${page}, ${amount}

mapper.insert:
insert into cart(iboard, iuser, color,
sm
md
lg
xl)
VALUES (${iboard}, ${iuser}, ${color},
${sm}
${md}
${lg}
${xl})

insert into cart(iboard, iuser, color, checked,
sm
md
lg
xl)
VALUES (${iboard}, ${iuser}, ${color}, 1,
${sm}
${md}
${lg}
${xl})

mapper.update:
UPDATE cart
SET checked = 1
WHERE icart = ${icart}

UPDATE cart
SET checked = 0
WHERE icart = ${icart}

mapper.insert:
insert into purchased(iboard, iuser, color,
sm
md
lg
xl)
VALUES (${iboard}, ${iuser}, ${color},
${sm}
${md}
${lg}
${xl})
